--
-- Definition of procedure `USP_ACT_RULES`
--

DROP PROCEDURE IF EXISTS `USP_ACT_RULES`;

DELIMITER $$

/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='' */ $$
CREATE  PROCEDURE `USP_ACT_RULES`(

  IN  P_ACTION            VARCHAR(3),
  IN  P_RULE_ID   INT,
  IN  P_PLUGIN_TYPE       VARCHAR(128),
  IN  P_HEADLINE_NAME     VARCHAR(576),
  IN  P_HEADLINE_NAME_O   Text,
  IN  P_DECUMENT          VARCHAR(128),
  IN  P_PUB_STATUS        INT,
  IN  P_CREATED_BY        INT,
  IN  P_ARC_STATUS        INT,
  IN  P_BLINK_STATUS      INT,
  OUT P_OUT               VARCHAR(256)



)
BEGIN

start transaction;
update t_actrule set INT_BLINK_STATUS=0 where INT_BLINK_STATUS=1 and BIT_DELETED_FLAG=0 and datediff(now(),DTM_CREATED_ON)>15;
commit;


  IF(P_ACTION='A')THEN
    INSERT INTO t_actrule (VCH_HEADLINE,VCH_HEADLINE_O, VCH_DOCUMENT, INT_PUBLISH_STATUS, INT_CREATED_BY, VCH_PLUGIN_TYPE,INT_ARC_STATUS,INT_BLINK_STATUS)
    VALUES(P_HEADLINE_NAME,P_HEADLINE_NAME_O,P_DECUMENT,P_PUB_STATUS,P_CREATED_BY,P_PLUGIN_TYPE,P_ARC_STATUS,P_BLINK_STATUS);
    SELECT "Notification Added Successfully";
  END IF;


  IF(P_ACTION='CD')THEN
    SET @P_SQL=CONCAT('SELECT VCH_HEADLINE FROM t_actrule WHERE BIT_DELETED_FLAG=0 AND VCH_HEADLINE="',P_HEADLINE_NAME,'"');

    IF(P_RULE_ID>0)THEN
      SET @P_SQL=CONCAT(@P_SQL,' AND INT_RULE_ID!=',P_RULE_ID);
    END IF;

   PREPARE STMT FROM @P_SQL;
   EXECUTE STMT;

  END IF;


  IF(P_ACTION='R')THEN
    SELECT INT_RULE_ID, VCH_HEADLINE,VCH_HEADLINE_O,INT_BLINK_STATUS, VCH_DOCUMENT, INT_PUBLISH_STATUS, DTM_CREATED_ON, INT_CREATED_BY,
     DTM_UPDATED_ON, INT_UPDATED_BY,  VCH_PLUGIN_TYPE,  INT_ARC_STATUS
      FROM t_actrule WHERE BIT_DELETED_FLAG=0 and INT_RULE_ID=P_RULE_ID;


  END IF;

  IF(P_ACTION='U')THEN
    UPDATE t_actrule SET
    VCH_PLUGIN_TYPE = P_PLUGIN_TYPE,
    VCH_HEADLINE    = P_HEADLINE_NAME,
    VCH_DOCUMENT    = P_DECUMENT,
    VCH_HEADLINE_O  = P_HEADLINE_NAME_O,
    INT_PUBLISH_STATUS    = P_PUB_STATUS,
    INT_UPDATED_BY        = P_CREATED_BY,
    INT_ARC_STATUS        = P_ARC_STATUS,
    INT_BLINK_STATUS      = P_BLINK_STATUS,
    DTM_UPDATED_ON        = NOW()
    WHERE  INT_RULE_ID=P_RULE_ID;
    SELECT "Updated Successfully";
  END IF;

  IF(P_ACTION='V')THEN
    SET @P_SQL='SELECT INT_RULE_ID,INT_BLINK_STATUS, VCH_HEADLINE,VCH_HEADLINE_O, VCH_DOCUMENT, INT_PUBLISH_STATUS, DTM_CREATED_ON, INT_CREATED_BY,
     DTM_UPDATED_ON, INT_UPDATED_BY,  VCH_PLUGIN_TYPE,  INT_ARC_STATUS FROM t_actrule WHERE BIT_DELETED_FLAG=0';

  IF(P_RULE_ID>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND INT_RULE_ID = "'),P_RULE_ID,'"');
    END IF;

  IF(CHAR_LENGTH(P_HEADLINE_NAME)>0)THEN
     SET @P_SQL  = CONCAT(@P_SQL,' AND VCH_HEADLINE LIKE "%',P_HEADLINE_NAME,'%"');
  END IF;

  IF(P_PLUGIN_TYPE>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND VCH_PLUGIN_TYPE = "'),P_PLUGIN_TYPE,'"');
    END IF;

  IF(P_ARC_STATUS>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND INT_ARC_STATUS = "'),P_ARC_STATUS,'"');
  END IF;
  IF(P_PUB_STATUS>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND INT_PUBLISH_STATUS = "'),P_PUB_STATUS,'"');
  END IF;

  SET @P_SQL=CONCAT(@P_SQL,' ORDER BY DTM_CREATED_ON DESC');

    PREPARE STMT FROM @P_SQL;
    EXECUTE STMT;
  END IF;



  IF(P_ACTION='PG')THEN
    SET @START_REC=P_RULE_ID;
     SET @P_SQL='SELECT INT_RULE_ID, INT_BLINK_STATUS,VCH_HEADLINE,VCH_HEADLINE_O, VCH_DOCUMENT, INT_PUBLISH_STATUS, DTM_CREATED_ON, INT_CREATED_BY,
     DTM_UPDATED_ON, INT_UPDATED_BY,  VCH_PLUGIN_TYPE,  INT_ARC_STATUS FROM t_actrule WHERE BIT_DELETED_FLAG=0';

  IF(P_RULE_ID>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND INT_RULE_ID = "'),P_RULE_ID,'"');
    END IF;

  IF(P_PLUGIN_TYPE>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND VCH_PLUGIN_TYPE = "'),P_PLUGIN_TYPE,'"');
    END IF;
  IF(P_ARC_STATUS>0) THEN
      SET @P_SQL=CONCAT(CONCAT(@P_SQL,' AND INT_ARC_STATUS = "'),P_ARC_STATUS,'"');
  END IF;

  IF(CHAR_LENGTH(P_HEADLINE_NAME)>0)THEN
     SET @P_SQL  = CONCAT(@P_SQL,' AND VCH_HEADLINE LIKE "%',P_HEADLINE_NAME,'%"');
  END IF;

  SET @P_SQL=CONCAT(@P_SQL,' ORDER BY DTM_CREATED_ON DESC LIMIT ?,20');

    PREPARE STMT FROM @P_SQL;
    EXECUTE STMT USING @START_REC;
  END IF;




  IF(P_ACTION='IN')THEN
    UPDATE t_actrule SET INT_PUBLISH_STATUS=1,INT_UPDATED_BY=P_CREATED_BY,DTM_UPDATED_ON=NOW()
    WHERE  INT_RULE_ID=P_RULE_ID AND BIT_DELETED_FLAG=0 ;
     SELECT "Unpublished Successfully";
  END IF;


  IF(P_ACTION='AR')THEN
    UPDATE t_actrule SET INT_ARC_STATUS=1,INT_UPDATED_BY=P_CREATED_BY,DTM_UPDATED_ON=NOW() WHERE  INT_RULE_ID=P_RULE_ID;
    SELECT "Archived Successfully";
  END IF;

   IF(P_ACTION='P')THEN
    UPDATE t_actrule SET INT_PUBLISH_STATUS=2,INT_UPDATED_BY=P_CREATED_BY,DTM_UPDATED_ON=NOW() WHERE  INT_RULE_ID=P_RULE_ID;
    SELECT "publish Successfully";
  END IF;


  IF(P_ACTION='D')THEN

	UPDATE t_actrule SET BIT_DELETED_FLAG=1 WHERE INT_RULE_ID=P_RULE_ID;
    SELECT '0';
  END IF;




  IF(P_ACTION = 'AC')THEN
     UPDATE t_actrule SET INT_ARC_STATUS = 2,DTM_UPDATED_ON = NOW(),INT_UPDATED_BY = P_CREATED_BY WHERE INT_RULE_ID=P_RULE_ID;
	 SELECT "Activated Successfully";
  END IF;


END $$
/*!50003 SET SESSION SQL_MODE=@TEMP_SQL_MODE */  $$

DELIMITER ;

