CREATE  PROCEDURE `USP_AUCTION_NOTICE`(

  IN  P_ACTION            VARCHAR(3),
  IN  P_NOTIFICATION_ID   INT,
  IN  P_LINK_TYPE         INT,
  IN  P_PLUGIN_TYPE       INT,
  IN  P_TEMPLATE_TYPE   SMALLINT(2),
  IN  P_WINDOW_TYPE     SMALLINT(2),
  IN  P_URL_TYPE        SMALLINT(2),
  IN  P_PLUGIN_ID       SMALLINT(2),
  IN  P_URL             VARCHAR(128),
  IN  P_HEADLINE_NAME     VARCHAR(256),
  IN  P_HEADLINE_NAME_O   VARCHAR(256),
  IN  P_DETAIL_E          mediumtext,
  IN  P_DETAIL_O          mediumtext,
  IN  P_CODE              VARCHAR(10),
  IN  P_DECUMENT          Text,
  IN  P_START_DATE        DATE,
  IN  P_DATE              DATE,
  IN  P_PUB_STATUS        INT,
  IN  P_CREATED_BY        INT,
  IN  P_ARC_STATUS        INT,
  IN  P_BLINK_STATUS      INT,
  IN  P_SL_NO             SMALLINT(9),
  OUT P_OUT               VARCHAR(256)


)
BEGIN



  IF(P_ACTION='A')THEN

    DELETE FROM t_auction_notice WHERE INT_AUCTION_ID=P_NOTIFICATION_ID;
    INSERT INTO t_auction_notice (INT_URL_TYPE,INT_WIN_STATUS,INT_TEMPLATE_TYPE,INT_PLUGIN_ID,VCH_URL,VCH_CODE,DTM_NOTICE_START,VCH_HEADLINE,VCH_HEADLINE_O,VCH_DETAILE,VCH_DETAILO, VCH_DOCUMENT,DTM_NOTICE_DATE, INT_PUBLISH_STATUS, INT_CREATED_BY, INT_PLUGIN_TYPE,INT_ARC_STATUS,INT_BLINK_STATUS,INT_SLNO)
    VALUES(P_URL_TYPE,P_WINDOW_TYPE,P_TEMPLATE_TYPE,P_PLUGIN_ID,P_URL,P_CODE,P_START_DATE,P_HEADLINE_NAME,P_HEADLINE_NAME_O,P_DETAIL_E,P_DETAIL_O,P_DECUMENT,P_DATE,P_PUB_STATUS,P_CREATED_BY,P_PLUGIN_TYPE,P_ARC_STATUS,P_BLINK_STATUS,P_SL_NO);

    
  END IF;



  IF(P_ACTION='CD')THEN
    SET @P_SQL=CONCAT('SELECT VCH_HEADLINE FROM t_auction_notice WHERE BIT_DELETED_FLAG=0  AND INT_LINK_TYPE= ',P_LINK_TYPE,' AND VCH_HEADLINE="',P_HEADLINE_NAME,'"');

    IF(P_NOTIFICATION_ID>0)THEN
      SET @P_SQL=CONCAT(@P_SQL,' AND INT_AUCTION_ID!=',P_NOTIFICATION_ID);
    END IF;

   PREPARE STMT FROM @P_SQL;
   EXECUTE STMT;

  END IF;



  IF(P_ACTION='R')THEN
      SELECT (SELECT C.VCH_RTO_OFFICENAME FROM m_rto_office C WHERE C.BIT_DELETED_FLAG=0 and  C.INT_RTO_ID=1 and C.INT_M_ID = N.INT_PLUGIN_TYPE) AS RTOofficeName,N.INT_URL_TYPE,N.INT_WIN_STATUS,N.INT_TEMPLATE_TYPE,N.INT_PLUGIN_ID,N.VCH_URL,N.VCH_CODE,N.DTM_NOTICE_START,N.INT_AUCTION_ID,N.VCH_HEADLINE,N.VCH_HEADLINE_O,N.VCH_DETAILE,N.VCH_DETAILO,N.INT_BLINK_STATUS,N.VCH_DOCUMENT,N.INT_PUBLISH_STATUS, N.DTM_CREATED_ON, N.INT_CREATED_BY,
