  SET @P_SQL='SELECT N.DTM_NOTICE_START,N.INT_LINK_TYPE,N.INT_URL_TYPE,N.INT_WIN_STATUS,N.INT_TEMPLATE_TYPE,N.INT_PLUGIN_ID,N.VCH_URL,N.VCH_CODE,N.INT_AUCTION_ID,N.INT_BLINK_STATUS, N.VCH_HEADLINE,N.VCH_HEADLINE_O,N.VCH_DETAILE,N.VCH_DETAILO, N.VCH_DOCUMENT, N.INT_PUBLISH_STATUS, N.DTM_CREATED_ON, N.INT_CREATED_BY,
