/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='' */ $$
CREATE  PROCEDURE `USP_DEALER_DONATION`(
  IN P_ACTION            VARCHAR(3),
  IN P_DEALER_ID         INT,
  IN P_CUSTOMER_ID       INT,
  IN P_TRANSACTION_ID    VARCHAR(64) CHARSET UTF8,  
  IN P_CREATED_ON        VARCHAR(32) CHARSET UTF8,
  IN P_CREATED_BY        INT,
  IN P_QUERY             VARCHAR(320) CHARSET UTF8,
  IN P_PAYAMT            DECIMAL(18,2),
  IN P_RTO               TINYINT,
  IN P_VEHICLENO         VARCHAR(128) CHARSET UTF8,
  IN P_RECEIPTNO         VARCHAR(128) CHARSET UTF8,
  IN P_FROM_DATE         VARCHAR(32) CHARSET UTF8,
  IN P_TO_DATE           VARCHAR(32) CHARSET UTF8,
  IN P_ATTR1             VARCHAR(256) CHARSET UTF8,
  IN P_ATTR2             VARCHAR(256) CHARSET UTF8,
  IN P_INTATTR1          INT,
  IN P_INTATTR2          INT,
  OUT P_OUT              VARCHAR(256) CHARSET UTF8
)
BEGIN
/* PROCEDURE NAME : USP_MANAGE_DEALER_DASHBOARD
   CREATED BY : Indrani
   CREATED ON : 20-01-2021
*/



/* Action to add customer details by dealer, by indrani on::20-01-2021 */
IF(P_ACTION='AC')THEN		
DROP TEMPORARY TABLE IF EXISTS TEMPBLOCK;
    CREATE TEMPORARY TABLE TEMPBLOCK
        (ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY, vchVehicleNo varchar(128), vchName varchar(256),intRTO tinyint(4),intVehicleCls tinyint(4),decAmount decimal(18,2), vchReceiptNo varchar(128));
    SET @P_SQL = CONCAT('INSERT INTO TEMPBLOCK (vchVehicleNo,vchName,intRTO,intVehicleCls,decAmount,vchReceiptNo) VALUES ',P_QUERY);
    PREPARE STMT FROM @P_SQL;
    EXECUTE STMT;
   
   SET @ROWCOUNT = (SELECT COUNT(ID) FROM TEMPBLOCK);
  
   SET @INTCOUNTER=1;
       
    WHILE(@INTCOUNTER<=@ROWCOUNT)
    DO
        SET @vchVehicleNo                    = (SELECT vchVehicleNo FROM TEMPBLOCK WHERE ID=@INTCOUNTER );        SET @vchName           	       = (SELECT vchName FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
        SET @intRTO           			     = (SELECT intRTO FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
		SET @intVehicleCls           	     = (SELECT intVehicleCls FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
        SET @decAmount                       = (SELECT decAmount FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
        SET @receiptNo                       = (SELECT vchReceiptNo FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
  
        INSERT INTO t_dealer_customer(intDealerId,vchVehicleNo,vchName,intRTO,intVehicleClass,decAmount,vchTransactionId,vchReceiptNo,stmCreatedOn,intCreatedBy)
		VALUES(P_DEALER_ID,@vchVehicleNo,@vchName,@intRTO,@intVehicleCls,@decAmount,P_TRANSACTION_ID,@receiptNo,NOW(),P_CREATED_BY);
       
        SET @INTCOUNTER = @INTCOUNTER+1;
    END WHILE;
    
END IF;

/* Action for storing dealer transaction by indrani on::22-01-2021 */
IF(P_ACTION='AUT')THEN

