--
-- Definition of procedure `USP_DISTRICT_MASTER`
--

DROP PROCEDURE IF EXISTS `USP_DISTRICT_MASTER`;

DELIMITER $$

/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='STRICT_TRANS_TABLES,NO_AUTO_CREATE_USER,NO_ENGINE_SUBSTITUTION' */ $$
CREATE  PROCEDURE `USP_DISTRICT_MASTER`(

		  IN  P_ACTION      	VARCHAR(3),

		  IN  P_DISTRICT_ID  	INT,

		  IN  P_DISTRICT_E 		VARCHAR(128),

		  IN  P_DISTRICT_O    	VARCHAR(256) CHARSET utf8,

          IN  P_DESCRIPTION		VARCHAR(512),

		  IN  P_DESCRIPTION_O   TEXT CHARSET utf8,

          IN  P_PUB_STATUS      TINYINT(3),

		  IN  P_CREATED_BY  	INT,

          IN  P_PAGE_SIZE  	    INT

		)
BEGIN		

		

        

	

		DECLARE EXIT HANDLER FOR SQLEXCEPTION

			BEGIN

			ROLLBACK;

			SELECT 'An error has occurred, operation rollbacked and the stored procedure was terminated';

		END;                      

	      

        

        

		  IF(P_ACTION='AU')THEN

          

              START TRANSACTION;

				IF(P_DISTRICT_ID>0)THEN

					SET @DUP_CTR = (SELECT COUNT(1) FROM m_district WHERE bitDeletedFlag=0 AND intDistrictid!=P_DISTRICT_ID AND vchDistrictname = P_DISTRICT_E );	  

					IF(@DUP_CTR =0)THEN            

						UPDATE m_district SET vchDistrictname = P_DISTRICT_E, vchDistrictnameO=P_DISTRICT_O,vchDescription = P_DESCRIPTION, vchDescriptionO=P_DESCRIPTION_O,intUpdatedBy=P_CREATED_BY WHERE intDistrictid = P_DISTRICT_ID;                

						SET @P_STATUS	= P_DISTRICT_ID;

					ELSE

						SET @P_STATUS	= 0;

				   END IF;   

				ELSE

				  SET @CTR = (SELECT COUNT(1) FROM m_district WHERE bitDeletedFlag=0 AND vchDistrictname = P_DISTRICT_E );	  

				  IF(@CTR =0)THEN 

					INSERT INTO m_district(vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby)VALUES (P_DISTRICT_E,P_DISTRICT_O,P_DESCRIPTION,P_DESCRIPTION_O,NOW(),P_CREATED_BY);

					SET @P_STATUS	= LAST_INSERT_ID();         

				  ELSE

					SET @P_STATUS	= 0;

				  END IF;            

				END IF;  

			 COMMIT;	

			 SELECT  @P_STATUS;        

			            

		  END IF;

          

		

		

         

		  IF(P_ACTION='V')THEN

			 SET @P_SQL='SELECT intDistrictid,vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby,tinPublishStatus FROM m_district WHERE bitDeletedFlag=0 ';	

		

				 IF(CHAR_LENGTH(P_DISTRICT_E)>0)THEN

						SET @P_SQL	= CONCAT(@P_SQL,' AND vchDistrictname LIKE "%',P_DISTRICT_E,'%"');

				 END IF;



		    SET @P_SQL=CONCAT(@P_SQL,' ORDER BY stmCreatedOn DESC '); 

			PREPARE STMT FROM @P_SQL;

			EXECUTE STMT;		

		  END IF;

		 

         

		

		IF(P_ACTION='PG') THEN

			  SET @START_REC=P_DISTRICT_ID;

			  SET @P_SQL='SELECT intDistrictid,vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby,tinPublishStatus FROM m_district WHERE bitDeletedFlag=0 ';	

		

				 IF(CHAR_LENGTH(P_DISTRICT_E)>0)THEN

						SET @P_SQL	= CONCAT(@P_SQL,' AND vchDistrictname LIKE "%',P_DISTRICT_E,'%"');

				 END IF;



		      SET @P_SQL=CONCAT(@P_SQL,' ORDER BY stmCreatedOn DESC LIMIT ?,',P_PAGE_SIZE);		

			  PREPARE STMT FROM @P_SQL;

			  EXECUTE STMT USING @START_REC;

	   END IF;

            

		 

		

		

	   IF(P_ACTION='CT')THEN

			SET @P_SQL  = 'SELECT count(1) AS COUNT FROM m_district WHERE bitDeletedFlag=0 ';

		

			 IF(CHAR_LENGTH(P_DISTRICT_E)>0)THEN

					SET @P_SQL	= CONCAT(@P_SQL,' AND vchDistrictname LIKE "%',P_DISTRICT_E,'%"');

			 END IF;



		    SET @P_SQL=CONCAT(@P_SQL,' ORDER BY stmCreatedOn DESC '); 

			PREPARE STMT FROM @P_SQL;

			EXECUTE STMT;		

	  END IF;

	 

		

	  IF(P_ACTION='D')THEN			

		 

           IF((SELECT COUNT(1) FROM m_block WHERE bitDeletedFlag= 0 AND intDistrictid = P_DISTRICT_ID)=0)THEN

			  UPDATE m_district SET bitDeletedFlag=1,intUpdatedBy = P_CREATED_BY WHERE intDistrictid = P_DISTRICT_ID;

			  SELECT "0";

			ELSE

			  SELECT 1;

		  END IF;

	  END IF;

			

	  IF(P_ACTION='R') THEN		

		  SELECT intDistrictid,vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby,tinPublishStatus FROM m_district WHERE bitDeletedFlag=0 AND intDistrictid = P_DISTRICT_ID ;		

	  END IF;

	 

	 

	  IF(P_ACTION='IN')THEN

		UPDATE m_district SET tinPublishstatus=2,intUpdatedBy=P_CREATED_BY WHERE intDistrictid = P_DISTRICT_ID ;		

		SELECT 'District Inactivated Successfully';

	  END IF;



	  IF(P_ACTION='AC')THEN

		UPDATE m_district SET tinPublishstatus=2,tinArchievestatus=0,intUpdatedBy=P_CREATED_BY WHERE intDistrictid = P_DISTRICT_ID;		

		SELECT 'District Activated Successfully';

	  END IF;

	  

	  IF(P_ACTION='P')THEN

		UPDATE m_district SET tinPublishstatus=1,tinArchievestatus=0,intUpdatedBy=P_CREATED_BY WHERE intDistrictid = P_DISTRICT_ID;		

		SELECT 'District published Successfully';

	  END IF;

	  

	  

	  IF(P_ACTION='F')THEN

		SET @P_SQL=CONCAT(' SELECT intDistrictid,vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby,tinPublishStatus,(select count(1) from t_institute_details TI where TI.bitDeletedFlag=0 and TI.tinPublishStatus=2 and TI.intDistrictId=md.intDistrictid and TI.tinIsPIA=',P_PAGE_SIZE);

        

        IF(P_CREATED_BY>0)THEN

			  	SET @P_SQL	= CONCAT(@P_SQL,'  and TI.intInstituteId IN (SELECT intInstituteId FROM t_institute_course TIC WHERE TIC.bitDeletedflag=0 AND TIC.intCourseId =',P_CREATED_BY,') ');

		END IF;        

        

        SET @P_SQL=CONCAT(@P_SQL,' ) as instCount FROM m_district md WHERE bitDeletedFlag=0 ');

        

			IF(P_DISTRICT_ID>0)THEN

			  SET @P_SQL=CONCAT(@P_SQL,' AND intDistrictid =',P_DISTRICT_ID);

			END IF;

            

             IF(CHAR_LENGTH(P_DISTRICT_E)>0)THEN

					SET @P_SQL	= CONCAT(@P_SQL,' AND intDistrictid IN (SELECT TI.intDistrictId FROM t_institute_details TI WHERE TI.bitDeletedFlag=0 AND TI.vchInstituteName LIKE "%',P_DISTRICT_E,'%" OR TI.intInstituteId IN (SELECT intInstituteId FROM t_institute_course TIC WHERE TIC.bitDeletedflag=0 AND TIC.intCourseId IN (select ts.intCourseId from t_course_details ts where ts.bitDeletedFlag=0  and ts.vchCourseNameE LIKE "%',P_DISTRICT_E,'%" )))');

			 END IF; 

             

             IF(P_CREATED_BY>0)THEN

			  	SET @P_SQL	= CONCAT(@P_SQL,' AND intDistrictid IN (SELECT TI.intDistrictId FROM t_institute_details TI WHERE TI.bitDeletedFlag=0 AND TI.tinIsPIA =',P_PAGE_SIZE,' AND TI.intInstituteId IN (SELECT intInstituteId FROM t_institute_course TIC WHERE TIC.bitDeletedflag=0 AND TIC.intCourseId =',P_CREATED_BY,'))');

			END IF;

                 

        SET @P_SQL=CONCAT(@P_SQL,' ORDER BY vchDistrictname asc ');	

        

       		PREPARE STMT FROM @P_SQL;

	    EXECUTE STMT;	

	  END IF;

      

          

	  IF(P_ACTION='MP')THEN

			SELECT MD.intDistrictid,MD.vchDistrictname,MD.vchDistrictnameO,vchSvgcx,vchSvgcy,vchSvgTransform,(select count(1) from t_institute_details TI where TI.intDistrictId =MD.intDistrictid and tinPublishStatus=2 and bitDeletedFlag=0 and TI.tinArcStatus=0 and tinIsPIA IN (1,3)) as instituteCount,(select count(1) from t_institute_details TI where TI.intDistrictId =MD.intDistrictid and tinPublishStatus=2 and bitDeletedFlag=0 and TI.tinArcStatus=0 and tinIsPIA =1) as totITICount,(select count(1) from t_institute_details TI where TI.intDistrictId =MD.intDistrictid and tinPublishStatus=2 and bitDeletedFlag=0 and TI.tinArcStatus=0 and tinIsPIA =3) as totPartnerCount  FROM m_district MD WHERE bitDeletedFlag=0 ;

	  END IF;	

      

      

       IF(P_ACTION='GMP')THEN

       

			SET @P_SQL='SELECT MD.intDistrictid,MD.vchDistrictname,MD.vchDistrictnameO,vchSvgcx,vchSvgcy,vchSvgTransform,(select count(1) from t_institute_details TI where TI.intDistrictId =MD.intDistrictid and TI.tinPublishStatus=2 and TI.bitDeletedFlag=0 and TI.tinArcStatus=0  ';

            

           

            

            IF(P_DISTRICT_ID>0)THEN

			  SET @P_SQL=CONCAT(@P_SQL,' AND TI.intInstituteId IN (SELECT intInstituteId FROM t_institute_course TIC WHERE TIC.bitDeletedflag=0 AND TIC.intCourseId =',P_DISTRICT_ID,') ');

			END IF;

            

            

            IF(P_PUB_STATUS>0)THEN

			  SET @P_SQL=CONCAT(@P_SQL,' AND  TI.tinIsPIA =',P_PUB_STATUS);

			END IF;

            

            

            SET @P_SQL	= CONCAT(@P_SQL,' ) as instituteCount ');

            

            SET @P_SQL	= CONCAT(@P_SQL,' FROM m_district MD WHERE bitDeletedFlag=0 ');	

           

            PREPARE STMT FROM @P_SQL;

			EXECUTE STMT;	

	  END IF;	

      

      

      IF(P_ACTION='VP')THEN

			 SET @P_SQL='SELECT intDistrictid,vchDistrictname,vchDistrictnameO,vchDescription,vchDescriptionO,stmCreatedOn,intCreatedby,tinPublishStatus FROM m_district WHERE bitDeletedFlag=0 ';	

		

				 IF(CHAR_LENGTH(P_DISTRICT_E)>0)THEN

						SET @P_SQL	= CONCAT(@P_SQL,' AND (vchDistrictname LIKE "%',P_DISTRICT_E,'%" OR vchDistrictnameO LIKE "%',P_DISTRICT_E,'%")');

				 END IF;



		    SET @P_SQL=CONCAT(@P_SQL,' ORDER BY vchDistrictname ASC '); 

			PREPARE STMT FROM @P_SQL;

			EXECUTE STMT;		

		  END IF;

      

END $$
