	IN	P_ACTION		VARCHAR(2),

	IN	P_DATE			DATE,

    IN	P_IP			VARCHAR(16)

)
BEGIN



IF(P_ACTION='A')THEN

	START TRANSACTION;

		SET @MAX_CTR	= (SELECT COALESCE(MAX(intHitCounter),0) FROM t_hit_counter);

        SET @MAX_CTR	= @MAX_CTR+1;

		SET @FLAG		= (SELECT COUNT(1) FROM t_hit_counter WHERE dteHitDate=P_DATE AND vchIPAddress=P_IP);

        DELETE FROM t_hit_counter WHERE dteHitDate < P_DATE;

        IF(@FLAG=0)THEN

			INSERT INTO t_hit_counter (dteHitDate,vchIPAddress,intHitCounter) VALUES (P_DATE,P_IP,@MAX_CTR);

        END IF;

        SELECT MAX(intHitCounter) FROM t_hit_counter;

    COMMIT;

END IF;

