BEGIN
/* PROCEDURE NAME : USP_MANAGE_DEALER_DASHBOARD
   CREATED BY : Indrani
   CREATED ON : 20-01-2021
*/

/* Action for dashboard details by indrani on::20-01-2021*/
IF(P_ACTION='VD')THEN
select decWalletAmt as decWalletAmt,(select count(1) from t_dealer_customer where intDealerId=P_DEALER_ID and bitDeletedFlag=0) as custCnt from t_dealer_registration where intDealerId=P_DEALER_ID and bitDeletedFlag=0;
END IF;

/* Action to add customer details by dealer, by indrani on::20-01-2021 */
IF(P_ACTION='AC')THEN		
DROP TEMPORARY TABLE IF EXISTS TEMPBLOCK;
    CREATE TEMPORARY TABLE TEMPBLOCK
        (ID INT NOT NULL AUTO_INCREMENT PRIMARY KEY, vchVehicleNo varchar(128), vchDonorName varchar(256),intRTO tinyint(4),intVehicleCls tinyint(4),decAmount decimal(18,2), vchReceiptNo varchar(128));
    SET @P_SQL = CONCAT('INSERT INTO TEMPBLOCK (vchVehicleNo,vchDonorName,intRTO,intVehicleCls,decAmount,vchReceiptNo) VALUES ',P_QUERY);
    PREPARE STMT FROM @P_SQL;
    EXECUTE STMT;
   
   SET @ROWCOUNT = (SELECT COUNT(ID) FROM TEMPBLOCK);
  
   SET @INTCOUNTER=1;
       
    WHILE(@INTCOUNTER<=@ROWCOUNT)
    DO
        SET @vchVehicleNo                    = (SELECT vchVehicleNo FROM TEMPBLOCK WHERE ID=@INTCOUNTER );        SET @vchDonorName           	       = (SELECT vchDonorName FROM TEMPBLOCK WHERE ID=@INTCOUNTER );
