/*!50003 SET @TEMP_SQL_MODE=@@SQL_MODE, SQL_MODE='' */ $$
CREATE  PROCEDURE `USP_SPECIAL_INFO`(
  IN P_ACTION            VARCHAR(3),
  IN P_INFO_ID           INT,
  IN P_TITLE             VARCHAR(500),
  IN P_START_DATE        DATE,
  IN P_END_DATE          DATE,
  IN P_PUBLISH_STATUS     INT,
  IN P_ARC_STATUS         INT,
  IN P_CREATED_BY         INT,
  IN P_ATTR1              VARCHAR(256),
  IN P_ATTR2              VARCHAR(256),
  IN P_INTATTR1           INT,
  IN P_INTATTR2           INT,
  OUT P_OUT               VARCHAR(256)
)
BEGIN
/* Procedure::USP_SPECIAL_INFO
   Created by::Indrani,On::26-11-2020 for Hoemage scrolling Information	
*/

IF(P_ACTION='A')THEN
  INSERT INTO t_special_info (VCH_TITLE,DTM_START_DATE,DTM_END_DATE,DTM_CREATED_ON,INT_CREATED_BY,INT_PUBLISH_STATUS,INT_ARCHIVE_STATUS)
      VALUES (P_TITLE,P_START_DATE,P_END_DATE,NOW(),P_CREATED_BY,P_PUBLISH_STATUS,P_ARC_STATUS);
