END IF;

IF(P_ACTION='U')THEN
  UPDATE t_special_info SET VCH_TITLE=P_TITLE,DTM_START_DATE=P_START_DATE,DTM_END_DATE=P_END_DATE,
		  DTM_UPDATED_ON=NOW(),INT_UPDATED_BY=P_CREATED_BY
	  WHERE INT_INFO_ID=P_INFO_ID AND BIT_DELETED_FLAG=0;
      SELECT "Special Information Updated Successfully";
END IF;

IF(P_ACTION='R')THEN
    SELECT INT_INFO_ID, VCH_TITLE, DTM_START_DATE, DTM_END_DATE,INT_PUBLISH_STATUS, INT_CREATED_BY,DTM_CREATED_ON FROM t_special_info WHERE INT_INFO_ID=P_INFO_ID AND BIT_DELETED_FLAG=0;
   END IF;
   
IF(P_ACTION='V')THEN
	SET @P_SQL='SELECT INT_INFO_ID, VCH_TITLE, DTM_START_DATE, DTM_END_DATE,INT_PUBLISH_STATUS, INT_CREATED_BY,DTM_CREATED_ON FROM t_special_info WHERE BIT_DELETED_FLAG=0';
