      SET @P_SQL=CONCAT(@P_SQL," AND INT_LOCATION_ID=",P_LOCATION_ID);

    END IF;



    IF(P_USER_ID>0)THEN

      SET @P_SQL=CONCAT(@P_SQL," AND VCH_USER_ID=",P_USER_ID);

    END IF;



